import React, { useState, useEffect } from 'react';
import './Forms.css';

const Forms = () => {
    const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('');
    const [shuffledAlphabet, setShuffledAlphabet] = useState([]);
    const [currentNameIndex, setCurrentNameIndex] = useState(0);
    const [currentEmailIndex, setCurrentEmailIndex] = useState(0);
    const [currentName, setCurrentName] = useState(localStorage.getItem('currentName') || "");
    const [currentEmail, setCurrentEmail] = useState(localStorage.getItem('currentEmail') || "");
    const [maritalStatus, setMaritalStatus] = useState(localStorage.getItem('maritalStatus') || "");
    const [pets, setPets] = useState(localStorage.getItem('pets') || "");
    const [zodiacSign, setZodiacSign] = useState(localStorage.getItem('zodiacSign') || "");
    const [ancestry, setAncestry] = useState(localStorage.getItem('ancestry') || "");
    const [birthDate, setBirthDate] = useState(localStorage.getItem('birthDate') || "");
    const [gender, setGender] = useState(localStorage.getItem('gender') || "masculin");
    const [attemptCount, setAttemptCount] = useState(0);
    const [confirmedFields, setConfirmedFields] = useState(JSON.parse(localStorage.getItem('confirmedFields')) || {
        name: false,
        email: false,
        maritalStatus: false,
        pets: false,
        zodiacSign: false,
        ancestry: false,
        birthDate: false,
        gender: false
    });

    useEffect(() => {
        setShuffledAlphabet(shuffleArray(alphabet));
    }, []);

    useEffect(() => {
        localStorage.setItem('currentName', currentName);
        localStorage.setItem('currentEmail', currentEmail);
        localStorage.setItem('maritalStatus', maritalStatus);
        localStorage.setItem('pets', pets);
        localStorage.setItem('zodiacSign', zodiacSign);
        localStorage.setItem('ancestry', ancestry);
        localStorage.setItem('birthDate', birthDate);
        localStorage.setItem('gender', gender);
        localStorage.setItem('confirmedFields', JSON.stringify(confirmedFields));
    }, [currentName, currentEmail, maritalStatus, pets, zodiacSign, ancestry, birthDate, gender, confirmedFields]);

    const shuffleArray = (array) => {
        let shuffledArray = array.slice();
        for (let i = shuffledArray.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
        }
        return shuffledArray;
    };

    const updateNameLetter = () => {
        return shuffledAlphabet[currentNameIndex];
    };

    const updateEmailLetter = () => {
        return shuffledAlphabet[currentEmailIndex];
    };

    const nextNameLetter = () => {
        setCurrentNameIndex((currentNameIndex + 1) % shuffledAlphabet.length);
    };

    const previousNameLetter = () => {
        setCurrentNameIndex((currentNameIndex - 1 + shuffledAlphabet.length) % shuffledAlphabet.length);
    };

    const nextEmailLetter = () => {
        setCurrentEmailIndex((currentEmailIndex + 1) % shuffledAlphabet.length);
    };

    const previousEmailLetter = () => {
        setCurrentEmailIndex((currentEmailIndex - 1 + shuffledAlphabet.length) % shuffledAlphabet.length);
    };

    const addNameLetter = () => {
        setCurrentName(currentName + shuffledAlphabet[currentNameIndex]);
    };

    const addEmailLetter = () => {
        setCurrentEmail(currentEmail + shuffledAlphabet[currentEmailIndex]);
    };

    const addSpace = () => {
        setCurrentName(currentName + " ");
    };

    const addEmailSymbol = (symbol) => {
        setCurrentEmail(currentEmail + symbol);
    };

    const clearName = () => {
        setCurrentName("");
    };

    const clearEmail = () => {
        setCurrentEmail("");
    };

    const clearMaritalStatus = () => {
        setMaritalStatus("");
    };

    const clearPets = () => {
        setPets("");
    };

    const clearZodiacSign = () => {
        setZodiacSign("");
    };

    const clearAncestry = () => {
        setAncestry("");
    };

    const clearBirthDate = () => {
        setBirthDate("");
    };

    const clearGender = () => {
        setGender("masculin");
        document.getElementById("sexe").value = "masculin";
    };

    const clearAll = () => {
        setCurrentName("");
        setCurrentEmail("");
        setMaritalStatus("");
        setPets("");
        setZodiacSign("");
        setAncestry("");
        setBirthDate("");
        setGender("masculin");
        document.getElementById("sexe").value = "masculin";
        document.getElementById("age-box").textContent = "Cliquez ici";
    };

    const randomAge = () => {
        const minAge = 10;
        const maxAge = 150;
        const randomAge = Math.floor(Math.random() * (maxAge - minAge + 1)) + minAge;
        document.getElementById("age-box").textContent = `${randomAge} ans`;
    };

    const openModal = () => {
        document.getElementById("myModal").style.display = "block";
    };

    const closeModal = () => {
        document.getElementById("myModal").style.display = "none";
    };

    const handleDateChange = (event) => {
        const selectedDate = new Date(event.target.value);
        selectedDate.setDate(selectedDate.getDate() - 1);
        setBirthDate(selectedDate.toISOString().split('T')[0]);
    };

    const confirmField = (field) => {
        if (window.confirm("Você realmente deseja confirmar esta informação?")) {
            setConfirmedFields({ ...confirmedFields, [field]: true });
        }
    };

    const attemptSubmit = () => {
        if (!currentName || !currentEmail || !maritalStatus || !pets || !zodiacSign || !ancestry) {
            alert("Il y a une erreur dans le formulaire.");
            return;
        }

        if (Object.values(confirmedFields).includes(false)) {
            alert("Toutes les réponses doivent être confirmées.");
            return;
        }

        setAttemptCount(attemptCount + 1);

        if (attemptCount < 3) {
            moveSubmitButton();
        } else {
            alert("Formulaire envoyé!");
            // Aqui você pode adicionar a lógica para enviar o formulário
        }
    };

    const moveSubmitButton = () => {
        const submitButton = document.getElementById("submit-btn");
        const maxX = window.innerWidth - submitButton.offsetWidth;
        const maxY = window.innerHeight - submitButton.offsetHeight;

        const randomX = Math.floor(Math.random() * maxX);
        const randomY = Math.floor(Math.random() * maxY);

        submitButton.style.position = "absolute";
        submitButton.style.left = `${randomX}px`;
        submitButton.style.top = `${randomY}px`;
    };

    return (
        <div className="form-container">
            <h2>Comment ne pas faire un formulaire</h2>

            <div className="button-section">
                <button 
                    type="button" 
                    className="submit-btn" 
                    id="submit-btn" 
                    onClick={attemptSubmit}
                >
                    Envoyer
                </button>
                <button 
                    type="button" 
                    className="clear-all-btn" 
                    onClick={clearAll}
                >
                    Effacer Tout
                </button>
            </div>

            <div className="age-section">
                <label>Âge généré:</label>
                <div id="age-box" className="age-box">Cliquez ici</div>
                <button type="button" className="age-button" onClick={randomAge}>Générer un âge mystérieux</button>
            </div>

            <div className="gender-section">
                <label htmlFor="sexe">Sexe:</label>
                <select id="sexe" name="sexe" value={gender} onChange={(e) => setGender(e.target.value)}>
                    <option value="masculin">Masculin</option>
                    <option value="féminin">Féminin</option>
                    <option value="autre">Autre</option>
                    <option value="preference-decliner">Préférer ne pas dire</option>
                </select>
                {!confirmedFields.gender && (
                    <>
                        <button type="button" className="clear-btn" onClick={clearGender}>Effacer</button>
                        <button type="button" className="confirm-btn" onClick={() => confirmField('gender')}>Confirmer</button>
                    </>
                )}
            </div>

            <div className="name-section">
                <label>Nom:</label>
                <div className="letter-selector">
                    <button onClick={previousNameLetter}>←</button>
                    <div id="letter" className="letter-box">{updateNameLetter()}</div>
                    <button onClick={nextNameLetter}>→</button>
                    <button onClick={addNameLetter}>Ajouter</button>
                    <button onClick={addSpace}>Espace</button>
                </div>
                <div className="name-preview">
                    Nom actuel: <span id="name-preview">{currentName || "_"}</span>
                </div>
                <button onClick={clearName} className="clear-btn">Effacer</button>
                {!confirmedFields.name && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('name')}>Confirmer</button>
                )}
            </div>

            <div className="email-section">
                <label>Email:</label>
                <div className="letter-selector">
                    <button onClick={previousEmailLetter}>←</button>
                    <div id="email-letter" className="letter-box">{updateEmailLetter()}</div>
                    <button onClick={nextEmailLetter}>→</button>
                    <button onClick={addEmailLetter}>Ajouter</button>
                    <button onClick={() => addEmailSymbol('@')}>@</button>
                    <button onClick={() => addEmailSymbol('.')}>.</button>
                </div>
                <div className="email-preview">
                    Email actuel: <span id="email-preview">{currentEmail || "_"}</span>
                </div>
                <button onClick={clearEmail} className="clear-btn">Effacer</button>
                {!confirmedFields.email && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('email')}>Confirmer</button>
                )}
            </div>

            <div className="marital-status-section">
                <label>État Civil<span className="required">*</span>:</label>
                <select value={maritalStatus} onChange={(e) => setMaritalStatus(e.target.value)} required>
                    <option value="">Sélectionnez</option>
                    <option value="célibataire">Célibataire</option>
                    <option value="marié">Marié</option>
                    <option value="divorcé">Divorcé</option>
                    <option value="veuf">Veuf</option>
                </select>
                <button onClick={clearMaritalStatus} className="clear-btn">Effacer</button>
                {!confirmedFields.maritalStatus && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('maritalStatus')}>Confirmer</button>
                )}
            </div>

            <div className="pets-section">
                <label>Combien d'animaux de compagnie avez-vous<span className="required">*</span>:</label>
                <input type="number" value={pets} onChange={(e) => setPets(e.target.value)} required />
                <button onClick={clearPets} className="clear-btn">Effacer</button>
                {!confirmedFields.pets && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('pets')}>Confirmer</button>
                )}
            </div>

            <div className="zodiac-sign-section">
                <label>Quel est votre signe du zodiaque<span className="required">*</span>:</label>
                <select value={zodiacSign} onChange={(e) => setZodiacSign(e.target.value)} required>
                    <option value="">Sélectionnez</option>
                    <option value="aries">Bélier</option>
                    <option value="taureau">Taureau</option>
                    <option value="gemeaux">Gémeaux</option>
                    <option value="cancer">Cancer</option>
                    <option value="lion">Lion</option>
                    <option value="vierge">Vierge</option>
                    <option value="balance">Balance</option>
                    <option value="scorpion">Scorpion</option>
                    <option value="sagittaire">Sagittaire</option>
                    <option value="capricorne">Capricorne</option>
                    <option value="verseau">Verseau</option>
                    <option value="poissons">Poissons</option>
                </select>
                <button onClick={clearZodiacSign} className="clear-btn">Effacer</button>
                {!confirmedFields.zodiacSign && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('zodiacSign')}>Confirmer</button>
                )}
            </div>

            <div className="ancestry-section">
                <label>Ascendance<span className="required">*</span>:</label>
                <input type="text" value={ancestry} onChange={(e) => setAncestry(e.target.value)} required />
                <button onClick={clearAncestry} className="clear-btn">Effacer</button>
                {!confirmedFields.ancestry && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('ancestry')}>Confirmer</button>
                )}
            </div>

            <div className="birthdate-section">
                <label>Date de Naissance:</label>
                <input type="date" value={birthDate} onChange={handleDateChange} />
                <button onClick={clearBirthDate} className="clear-btn">Effacer</button>
                {!confirmedFields.birthDate && (
                    <button type="button" className="confirm-btn" onClick={() => confirmField('birthDate')}>Confirmer</button>
                )}
            </div>

            <img 
                src="https://example.com/turtle-image.png" 
                alt="Tartaruga" 
                className="turtle-img" 
                onClick={openModal}
            />

            <div id="myModal" className="modal">
                <div className="modal-content">
                    <span className="close" onClick={closeModal}>×</span>
                    <h3>Qui est cette créature?</h3>
                    <p>Les informations sont secrètes. Appuyez sur "OK" pour découvrir quelque chose.</p>
                </div>
            </div>
        </div>
    );
};

export default Forms;
