import React, { useState } from 'react';

const Forms = () => {
    const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    const [currentIndex, setCurrentIndex] = useState(0);
    const [currentName, setCurrentName] = useState("");
    const [attemptCount, setAttemptCount] = useState(0);

    const updateLetter = () => {
        return alphabet[currentIndex];
    };

    const nextLetter = () => {
        setCurrentIndex((currentIndex + 1) % alphabet.length);
    };

    const previousLetter = () => {
        setCurrentIndex((currentIndex - 1 + alphabet.length) % alphabet.length);
    };

    const addLetter = () => {
        setCurrentName(currentName + alphabet[currentIndex]);
    };

    const randomAge = () => {
        const minAge = 10;
        const maxAge = 150;
        const randomAge = Math.floor(Math.random() * (maxAge - minAge + 1)) + minAge;
        document.getElementById("age-box").textContent = `${randomAge} ans`;
    };

    const openModal = () => {
        document.getElementById("myModal").style.display = "block";
    };

    const closeModal = () => {
        document.getElementById("myModal").style.display = "none";
    };

    const attemptSubmit = () => {
        setAttemptCount(attemptCount + 1);

        if (attemptCount < 3) {
            moveSubmitButton();
        } else {
            alert("Formulário enviado!");
        }
    };

    const moveSubmitButton = () => {
        const submitButton = document.getElementById("submit-btn");
        const maxX = window.innerWidth - submitButton.offsetWidth;
        const maxY = window.innerHeight - submitButton.offsetHeight;

        const randomX = Math.floor(Math.random() * maxX);
        const randomY = Math.floor(Math.random() * maxY);

        submitButton.style.position = "absolute";
        submitButton.style.left = `${randomX}px`;
        submitButton.style.top = `${randomY}px`;
    };

    return (
        <div style={styles.formContainer}>
            <h2>Formulaire Incompréhensible</h2>

            <label>Nom:</label>
            <div style={styles.letterSelector}>
                <button onClick={previousLetter}>←</button>
                <div id="letter" style={styles.letterBox}>{updateLetter()}</div>
                <button onClick={nextLetter}>→</button>
                <button onClick={addLetter}>Ajouter</button>
            </div>

            <div style={styles.namePreview}>
                Nom actuel: <span id="name-preview">{currentName || "_"}</span>
            </div>

            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" required placeholder="S'il vous plaît, entrez quelque chose" style={styles.input} />

            <label htmlFor="sexe">Sexe:</label>
            <select id="sexe" name="sexe" style={styles.input}>
                <option value="masculin">Masculin</option>
                <option value="féminin">Féminin</option>
                <option value="autre">Autre</option>
                <option value="preference-decliner">Préférer ne pas dire</option>
            </select>

            <div style={styles.ageSelector}>
                <label>Âge généré:</label>
                <div id="age-box" style={styles.ageBox}>Cliquez ici</div>
                <button type="button" style={styles.ageButton} onClick={randomAge}>Générer un âge mystérieux</button>
            </div>

            <button type="submit" style={styles.submitBtn} id="submit-btn" onClick={attemptSubmit}>Envoyer</button>

            <img 
                src="https://example.com/turtle-image.png" 
                alt="Tartaruga" 
                style={styles.turtleImg} 
                onClick={openModal}
            />

            <div id="myModal" style={styles.modal}>
                <div style={styles.modalContent}>
                    <span style={styles.close} onClick={closeModal}>×</span>
                    <h3>Qui est cette créature?</h3>
                    <p>Les informations sont secrètes. Appuyez sur "OK" pour découvrir quelque chose.</p>
                </div>
            </div>
        </div>
    );
};

const styles = {
    formContainer: {
        backgroundColor: '#fff',
        padding: '20px',
        margin: '50px auto',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
        maxWidth: '500px',
        fontFamily: 'Arial, sans-serif'
    },
    letterSelector: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: '15px'
    },
    letterBox: {
        border: '1px solid #ccc',
        padding: '10px',
        width: '50px',
        textAlign: 'center'
    },
    namePreview: {
        marginBottom: '15px'
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '15px',
        border: '1px solid #ccc',
        borderRadius: '4px'
    },
    ageSelector: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: '15px'
    },
    ageBox: {
        border: '1px solid #ccc',
        padding: '10px',
        width: '50px',
        textAlign: 'center'
    },
    ageButton: {
        padding: '10px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        backgroundColor: '#4CAF50',
        color: 'white'
    },
    submitBtn: {
        backgroundColor: '#4CAF50',
        color: 'white',
        padding: '10px 20px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        display: 'block',
        width: '100%'
    },
    turtleImg: {
        display: 'block',
        margin: '20px auto',
        cursor: 'pointer',
        maxWidth: '100%'
    },
    modal: {
        display: 'none',
        position: 'fixed',
        zIndex: 1,
        left: 0,
        top: 0,
        width: '100%',
        height: '100%',
        overflow: 'auto',
        backgroundColor: 'rgba(0, 0, 0, 0.4)',
        paddingTop: '60px'
    },
    modalContent: {
        backgroundColor: '#fefefe',
        margin: '5% auto',
        padding: '20px',
        border: '1px solid #888',
        width: '80%',
        maxWidth: '500px',
        borderRadius: '8px'
    },
    close: {
        color: '#aaa',
        float: 'right',
        fontSize: '28px',
        fontWeight: 'bold',
        cursor: 'pointer'
    }
};

export default Forms;
