import React, { useState, useEffect } from 'react';
import './Forms.css';

const Forms = () => {
    const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('');
    const [shuffledAlphabet, setShuffledAlphabet] = useState([]);
    const [currentNameIndex, setCurrentNameIndex] = useState(0);
    const [currentEmailIndex, setCurrentEmailIndex] = useState(0);
    const [currentName, setCurrentName] = useState("");
    const [currentEmail, setCurrentEmail] = useState("");
    const [attemptCount, setAttemptCount] = useState(0);

    useEffect(() => {
        setShuffledAlphabet(shuffleArray(alphabet));
    }, []);

    const shuffleArray = (array) => {
        let shuffledArray = array.slice();
        for (let i = shuffledArray.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
        }
        return shuffledArray;
    };

    const updateNameLetter = () => {
        return shuffledAlphabet[currentNameIndex];
    };

    const updateEmailLetter = () => {
        return shuffledAlphabet[currentEmailIndex];
    };

    const nextNameLetter = () => {
        setCurrentNameIndex((currentNameIndex + 1) % shuffledAlphabet.length);
    };

    const previousNameLetter = () => {
        setCurrentNameIndex((currentNameIndex - 1 + shuffledAlphabet.length) % shuffledAlphabet.length);
    };

    const nextEmailLetter = () => {
        setCurrentEmailIndex((currentEmailIndex + 1) % shuffledAlphabet.length);
    };

    const previousEmailLetter = () => {
        setCurrentEmailIndex((currentEmailIndex - 1 + shuffledAlphabet.length) % shuffledAlphabet.length);
    };

    const addNameLetter = () => {
        setCurrentName(currentName + shuffledAlphabet[currentNameIndex]);
    };

    const addEmailLetter = () => {
        setCurrentEmail(currentEmail + shuffledAlphabet[currentEmailIndex]);
    };

    const addSpace = () => {
        setCurrentName(currentName + " ");
    };

    const addEmailSymbol = (symbol) => {
        setCurrentEmail(currentEmail + symbol);
    };

    const clearName = () => {
        setCurrentName("");
    };

    const clearEmail = () => {
        setCurrentEmail("");
    };

    const clearAll = () => {
        setCurrentName("");
        setCurrentEmail("");
        document.getElementById("sexe").value = "masculin";
        document.getElementById("age-box").textContent = "Cliquez ici";
    };

    const randomAge = () => {
        const minAge = 10;
        const maxAge = 150;
        const randomAge = Math.floor(Math.random() * (maxAge - minAge + 1)) + minAge;
        document.getElementById("age-box").textContent = `${randomAge} ans`;
    };

    const openModal = () => {
        document.getElementById("myModal").style.display = "block";
    };

    const closeModal = () => {
        document.getElementById("myModal").style.display = "none";
    };

    const attemptSubmit = () => {
        setAttemptCount(attemptCount + 1);

        if (attemptCount < 3) {
            moveSubmitButton();
        } else {
            alert("Formulário envoyé!");
            // Aqui você pode adicionar a lógica para enviar o formulário
        }
    };

    const moveSubmitButton = () => {
        const submitButton = document.getElementById("submit-btn");
        const maxX = window.innerWidth - submitButton.offsetWidth;
        const maxY = window.innerHeight - submitButton.offsetHeight;

        const randomX = Math.floor(Math.random() * maxX);
        const randomY = Math.floor(Math.random() * maxY);

        submitButton.style.position = "absolute";
        submitButton.style.left = `${randomX}px`;
        submitButton.style.top = `${randomY}px`;
    };

    return (
        <div className="form-container">
            <h2>Comment ne pas faire un formulaire</h2>

            <div className="button-section">
                <button 
                    type="button" 
                    className="submit-btn" 
                    id="submit-btn" 
                    onClick={attemptSubmit}
                >
                    Envoyer
                </button>
                <button 
                    type="button" 
                    className="clear-all-btn" 
                    onClick={clearAll}
                >
                    Effacer Tout
                </button>
            </div>

            <div className="age-section">
                <label>Âge généré:</label>
                <div id="age-box" className="age-box">Cliquez ici</div>
                <button type="button" className="age-button" onClick={randomAge}>Générer un âge mystérieux</button>
            </div>

            <div className="gender-section">
                <label htmlFor="sexe">Sexe:</label>
                <select id="sexe" name="sexe">
                    <option value="masculin">Masculin</option>
                    <option value="féminin">Féminin</option>
                    <option value="autre">Autre</option>
                    <option value="preference-decliner">Préférer ne pas dire</option>
                </select>
            </div>

            <div className="name-section">
                <label>Nom:</label>
                <div className="letter-selector">
                    <button onClick={previousNameLetter}>←</button>
                    <div id="letter" className="letter-box">{updateNameLetter()}</div>
                    <button onClick={nextNameLetter}>→</button>
                    <button onClick={addNameLetter}>Ajouter</button>
                    <button onClick={addSpace}>Espace</button>
                </div>
                <div className="name-preview">
                    Nom actuel: <span id="name-preview">{currentName || "_"}</span>
                </div>
                <button onClick={clearName} className="clear-btn">Effacer</button>
            </div>

            <div className="email-section">
                <label>Email:</label>
                <div className="letter-selector">
                    <button onClick={previousEmailLetter}>←</button>
                    <div id="email-letter" className="letter-box">{updateEmailLetter()}</div>
                    <button onClick={nextEmailLetter}>→</button>
                    <button onClick={addEmailLetter}>Ajouter</button>
                    <button onClick={() => addEmailSymbol('@')}>@</button>
                    <button onClick={() => addEmailSymbol('.')}>.</button>
                </div>
                <div className="email-preview">
                    Email actuel: <span id="email-preview">{currentEmail || "_"}</span>
                </div>
                <button onClick={clearEmail} className="clear-btn">Effacer</button>
            </div>

            <img 
                src="https://example.com/turtle-image.png" 
                alt="Tartaruga" 
                className="turtle-img" 
                onClick={openModal}
            />

            <div id="myModal" className="modal">
                <div className="modal-content">
                    <span className="close" onClick={closeModal}>×</span>
                    <h3>Qui est cette créature?</h3>
                    <p>Les informations sont secrètes. Appuyez sur "OK" pour découvrir quelque chose.</p>
                </div>
            </div>
        </div>
    );
};

export default Forms;
